import { Component, Input, Output, EventEmitter } from '@angular/core';
import { Contact } from 'src/app/models/contacts';
import { ContactService } from 'src/app/services/contact.service';

@Component({
  selector: 'app-edit-contact',
  templateUrl: './edit-contact.component.html',
  styleUrls: ['./edit-contact.component.scss']
})
export class EditContactComponent {
  @Input() contact?: Contact
  @Output() contactUpdated = new EventEmitter<Contact[]>();

  constructor(private contactService: ContactService) {}

  // Calls service to update the contact
  updateContact(contact: Contact) {
    this.contactService.updateContact(contact)
    .subscribe((contacts: Contact[]) => this.contactUpdated.emit(contacts));
  }

  // Calls service to create a contact
  createContact(contact: Contact) {
    this.contactService.createContact(contact)
    .subscribe((contacts: Contact[]) => this.contactUpdated.emit(contacts));
  }

  // Calls service to delete the contact
  deleteContact(contact: Contact) {
    this.contactService.deleteContact(contact)
    .subscribe((contacts: Contact[]) => this.contactUpdated.emit(contacts));
  }
}
<div *ngIf="contact">
    <h2>{{ contact.firstName }}</h2>
    <div>
        First Name:
        <input [(ngModel)]="contact.firstName" placeholder="First Name"/>
    </div>
    <div>
        Last Name:
        <input [(ngModel)]="contact.lastName" placeholder="Last Name"/>
    </div>
    <div>
        Email:
        <input [(ngModel)]="contact.email" placeholder="Email"/>
    </div>
    <div>
        Phone Number:
        <input [(ngModel)]="contact.phoneNumber" placeholder="Phone Number"/>
    </div>

    <button class="btn btn-success" (click)="updateContact(contact)" *ngIf="contact.id">Save</button>
    <button class="btn btn-danger"(click)="deleteContact(contact)" *ngIf="contact.id">Delete</button>
    <button class="btn btn-success" (click)="createContact(contact)" *ngIf="!contact.id">Create</button>

</div>
