import { Component, Input } from '@angular/core';
import { Contact } from 'src/app/models/contacts';
import { ContactService } from 'src/app/services/contact.service';

@Component({
  selector: 'app-contact-list',
  templateUrl: './contact-list.component.html',
  styleUrls: ['./contact-list.component.scss']
})
export class ContactListComponent {
  contacts: Contact[] = [];
  @Input()contact!: Contact;


  constructor(private contactService: ContactService) {}

  initNewContact() {
    this.contact = new Contact();
  }

  editContact(contact: Contact) {
    this.contact = contact;
  }

  ngOnInit() : void {
    this.contactService.getContacts().subscribe((result: Contact[]) => (this.contacts = result));
  }
}
<button (click)="initNewContact()">Create New Contact</button>

<table class="table">
    <thead>
      <tr>
        <th scope="col">First Name</th>
        <th scope="col">Last Name</th>
        <th scope="col">Email</th>
        <th scope="col">Phone Number</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let contact of contacts">
        <td>{{contact.firstName}}</td>
        <td>{{contact.lastName}}</td>
        <td>{{contact.email}}</td>
        <td>{{contact.phoneNumber}}</td>
        <td><button (click)="editContact(contact)">Edit</button>></td>
      </tr>
    </tbody>
  </table>
